  header {
    h1 {
      font-size: 4rem;
    }
    h4 {
      font-size: 1.2rem;
      padding-bottom: 3vh;
    }
  }
}

@media only screen and (max-device-width: 874px) {
  aside {
    width: 16rem;
    h2 {
      text-align: center;
      padding: 0.5rem 0vw;
      margin: 0px 3rem;
    }
  }
}

@media only screen and (max-device-width: 796px) {
  .modalMainContainer {
    width: 100vw;
    .modalContentContainer {
      margin: 2vw auto;
      width: 90vw;
      .modalHeader {
        padding: 1vw;
        font-size: 1.5rem;
      }
      .modalBody {
        padding: 5vw;
        font-size: 1.25rem;
        input {
          height: 3rem;
          font-size: 2rem;
        }
      }
    }
  }
}

@media only screen and (max-device-width: 684px) {
  .boxes {
    padding: 2vh 0vw;
    grid-gap: 3vw;
    grid-auto-rows: minmax(8vw, 150px);
    grid-template-columns: repeat(auto-fit, 1fr);
    .card {
      transition: transform 500ms;
    }
  }
}

@media only screen and (max-device-width: 618px) {
  header {
    h1 {
      font-size: 3rem;
    }
    h4 {
      font-size: 1.1rem;
      padding-bottom: 3vh;
    }
  }
}

@media only screen and (max-device-width: 592px) {
  // ! Sidebar
  aside {
    position: static;
    width: 100vw;
    ul {
      li {
        font-size: 1.1rem;
      }
    }
  }

  main {
    width: 100vw;
    margin: 0;
  }

