// ! Global Class Variables

.hide {
  display: none;
}

// ! For better hover
.betterHover {
  a {
    transform: translate(-0.001%, -0.001%);
    width: 100%;
    height: 100%;
    text-align: center;
    line-height: 60px;
    background: linear-gradient(90deg, #03a9f4, #f441a5, #ffeb3b, #03a9f4);
    background-size: 400%;
    border-radius: 10px;
    z-index: 1;
  }
  a:hover {
    animation: animate 5s linear infinite;
  }
  @keyframes animate {
    0% {
      background-position: 0%;
    }
    100% {
      background-position: 400%;
    }
  }
  a:before {
    content: '';
    position: absolute;
    top: -5px;
    left: -5px;
    right: -5px;
    bottom: -5px;
    z-index: -1;
    background: linear-gradient(90deg, #03a9f4, #f441a5, #ffeb3b, #03a9f4);
    background-size: 400%;
    border-radius: 40px;
    opacity: 0;
    transition: 0.5s;
  }
  a:hover:before {
    filter: blur(20px);
    opacity: 1;
    animation: animate 5s linear infinite;
  }
}

// ! For barrell roll button
.rotated {
  -ms-transform: rotate(360deg); /* IE 9 */
  -webkit-transform: rotate(360deg); /* Chrome, Safari, Opera */
  transform: rotate(360deg);
}

// ! Used for lights dimmer
.dimScreen {
  * {
    filter: brightness(0%);
  }
  body {
    background: linear-gradient(#000000c9, #000000c9),
      url(../images/cityNight.jpg) no-repeat center center fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
    color: $outlineText;
  }
}

//! Used for button killing
.killButtons {
  transform: scale(0);
}

//! Used for button glow effect with nav
div.card.buttonGlow {
  box-shadow: 0 0 10px #fff, /* outer white */ -10px 0 40px #f0f,
    /* outer left magenta */ 10px 0 40px #0ff; /* outer right cyan */
}

.lightsOff {
  * {
    opacity: !important 0;
    background-color: black;
  }
}

//! Used for Modal loading effect
.LoaderBalls {
  margin: 12vh 90px 5vh;
  width: 90px;
  // height: 10vh;
	display: flex;
	justify-content: space-between;
	align-items: center;

	&__item {
		// .LoaderBalls__wrapper__item
		$anim-drt: 0.4s;
		$anim-ease: cubic-bezier(.6,.05,.15,.95);
		width: 20px;
		height: 20px;
		border-radius: 50%;
		background: #00f1ca;

		&:nth-child(1) {
			animation: bouncing $anim-drt alternate infinite $anim-ease;
		}

		&:nth-child(2) {
			animation: bouncing $anim-drt $anim-drt/4 alternate infinite $anim-ease backwards;
		}

		&:nth-child(3) {
			animation: bouncing $anim-drt $anim-drt/2 alternate infinite $anim-ease backwards;
		}
	}
}

@keyframes bouncing {

	0% {
		transform: translate3d(0, 10px, 0) scale(1.2, 0.85);
	}

	100% {
		transform: translate3d(0, -20px, 0) scale(0.9, 1.1);
	}
}

